function FindQuestions(HTML: TDomTree): TArray<string>;
begin
  var xpath := './/a[class="question__title-link question__title-link_list"]';
  Result := HTML.RootNode.FindPathAttributes(xpath, 'href');
end;

function FindNextPage(HTML: TDomTree): string;
begin
  var xpath := './/li[class="paginator__item next"]/a';
  Result := HTML.RootNode.FindPathOne(xpath).Attributes['href'].Trim(['"']);
end;

function TextToHtml(Value: string): TDomTree;
begin
  Result := TDomTree.FromString(Value);
end;

procedure ParseQuestion(HTML: TDomTree);
begin
  var title := HTML.RootNode.FindPathOne('.//h1[class="question__title"]').Child[0].Text.Trim;
  var body := HTML.RootNode.FindPathOne('.//meta[name="description"]').Attributes['content'].Trim.Trim(['"']);
  Writeln(title + #13#10#13#10 + body);
  Writeln('----------------------------------------');
end;

