`enum Tag {
  Just,
  Nothing
}

struct Maybe<T>
{
  private T value;
  public Tag Tag { get; private set;}  
  public T Value => Tag == Tag.Just ? value : throw new Exception();

  public static Maybe<T> Just(T t) => new Maybe<T> {value = t, Tag = Tag.Just};
  public static Maybe<T> Nothing() => new Maybe<T> {Tag = Tag.Nothing};
}`

