function gitcmd($1, $2, $3, $4, $5, $6, $7, $8)
{
  $cmdline = "git"

  $args = @($1, $2, $3, $4, $5, $6, $7, $8)
  foreach ($_ in $args)
  {
    if (($_ -ne $null) -and ($_ -ne ""))
    {
      $cmdline += " `"$_`""
    }  
  }

  Invoke-Expression $cmdline
  if ($LASTEXITCODE -ne 0)
  {
    Write-Host "Exit code $LASTEXITCODE" -ForegroundColor Red
    Exit $LASTEXITCODE
  }
}`

