`use std::time::Duration;

use libusb;
use libusb::Device;

use libusb_sys::LIBUSB_ENDPOINT_IN;

fn main() -> () {
    let context = libusb::Context::new().unwrap();

    let mut uniden: Option<Device> = None;

    for device in context.devices().unwrap().iter() {
        let device_desc = device.device_descriptor().unwrap();

        /* 
        println!("Bus {:03} Device {:03} ID {:04x}:{:04x}",
                 device.bus_number(),
                 device.address(),
                 device_desc.vendor_id(),
                 device_desc.product_id());
        */

        if format!("{:04x}", device_desc.product_id()) == "2303" {
            uniden = Some(device);

            println!("Vendor id {}", device_desc.vendor_id());
        }
    }

    let clean_uniden = uniden.unwrap();

    match clean_uniden.open() {
        Ok(handle) => {
            let time_out = Duration::from_secs(1);

            let buf: &[u8] = "VER\r\n".as_bytes();

            let res = handle.write_interrupt(LIBUSB_ENDPOINT_IN , buf, time_out);
            //let res = handle.write_bulk(LIBUSB_ENDPOINT_IN, buf, time_out);

            print!("Result {:?}", res);
        },

        Err(err) => println!("Some error {:?}", err)
    };
}`
