```CREATE FUNCTION public.trg_partition()
    RETURNS trigger
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE LEAKPROOF
AS $BODY$
DECLARE
prefix text := 'partitions.';
timeformat text;
selector text;
_interval interval;
tablename text;
startdate text;
enddate text;
create_table_part text;
create_index_part1 text;
create_index_part2 text;
create_index_part3 text;
create_index_part4 text;
create_index_part5 text;

BEGIN
selector = TG_ARGV[0];

IF selector = 'day' THEN
timeformat := 'YYYY_MM_DD';
ELSIF selector = 'month' THEN
timeformat := 'YYYY_MM';
END IF;

_interval := '1' || selector;
tablename :=  TG_TABLE_NAME || '_p' || to_char(NEW.date_time, timeformat);

IF ((select count(*) from TG_TABLE_NAME where id = NEW.id) = 0)
THEN
EXECUTE 'INSERT INTO ' || prefix || tablename || ' SELECT ($1).* returning id' USING NEW;
RETURN NEW;
END IF;

EXCEPTION 
when undefined_table then
startdate := NEW.date_time::date;
enddate :=NEW.date_time::date + _interval;

create_table_part:= 'CREATE TABLE IF NOT EXISTS '|| prefix || quote_ident(tablename) || ' (CONSTRAINT '|| quote_ident(tablename) ||'_pkey PRIMARY KEY (id),
CONSTRAINT '|| quote_ident(tablename) ||'_date_time_check CHECK ((date_time >= ''' || startdate::timestamp  || ''' AND date_time < ''' || enddate::timestamp || '''))) INHERITS ('|| TG_TABLE_NAME || ')';
create_index_part1:= 'CREATE INDEX '|| quote_ident(tablename) || 'ix_monitoring_client_token on ' || prefix || quote_ident(tablename) || ' USING btree(client_token)';
create_index_part2:= 'CREATE INDEX '|| quote_ident(tablename) || 'ix_monitoring_client_token_user_date_time on ' || prefix || quote_ident(tablename) || ' USING btree("user", client_token, date_time)';
create_index_part3:= 'CREATE INDEX '|| quote_ident(tablename) || 'ix_monitoring_client_version on ' || prefix || quote_ident(tablename) || ' USING btree(client_version COLLATE pg_catalog."default")';
create_index_part4:= 'CREATE INDEX '|| quote_ident(tablename) || 'ix_monitoring_date_time on ' || prefix || quote_ident(tablename) || ' USING btree(date_time)';
create_index_part5:= 'CREATE INDEX '|| quote_ident(tablename) || 'ix_monitoring_user on ' || prefix || quote_ident(tablename) || ' USING btree("user")';

EXECUTE create_table_part;
EXECUTE create_index_part1;
EXECUTE create_index_part2;
EXECUTE create_index_part3;
EXECUTE create_index_part4;
EXECUTE create_index_part5;
--insert it again
EXECUTE 'INSERT INTO ' || prefix || tablename || ' SELECT ($1).* returning id' USING NEW;
RETURN NEW;
END;
$BODY$;


CREATE TRIGGER partition_trg
    AFTER INSERT
    ON public.monitoring
    FOR EACH ROW
    EXECUTE PROCEDURE public.trg_partition('day’);```
